import React, {useState,useEffect} from 'react';
import Problem from '../../components/Problem/Problem';
import './Problems.css';

function sortTableByQuestionCount() {
    let table = document.getElementById("problemsTable");
    let rows = Array.from(table.rows).slice(1); 

    rows.sort((a, b) => {
        let questiosA = parseInt(a.cells[3].innerText);
        let questionsB = parseInt(b.cells[3].innerText);
        return questionsB - questiosA; 
    });

    let tbody = table.tBodies[0];
    tbody.append(...rows);
}

const Problems = ({author}) => {
    const [problems, setProblems] = useState([]);

    useEffect(()=>{
        // console.log("Loading Data ...")
        sortTableByQuestionCount();
        fetch(`http://localhost:8081/problems/${author}`)
            .then(response=>response.json())
            .then(data=>setProblems(data))
    },)

    if (!author)
        return (
            <div>
                <main>
                    <h1>All Problems</h1>
                    <table id="problemsTable">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Content</th>
                                <th>Difficulty</th>
                                <th>Solved #</th>
                                <th>Category</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                            problems.map((problem)=>{
                                return <Problem key={problem.id} title={problem.title} difficulty={problem.difficulty} solved={problem.solved} category={problem.category} content={problem.content} option_1={problem.option_1} option_2={problem.option_2} option_3={problem.option_3} option_4={problem.option_4}/>
                            })
                            }
                        </tbody>
                    </table>
                </main>
            </div>
    );

    // teacher profile
    return (
        <div>
            <main>
                <h2>Questions Designed</h2>
                <table id="problemsTable">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Content</th>
                            <th>Difficulty</th>
                            <th>Solved #</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        problems.map((problem)=>{
                            return <Problem key={problem.id} title={problem.title} difficulty={problem.difficulty} solved={problem.solved} category={problem.category} content={problem.content} option_1={problem.option_1} option_2={problem.option_2} option_3={problem.option_3} option_4={problem.option_4}/>
                        })
                        }
                    <tr>
                        <td>title</td>
                        <td><p>content</p></td>
                        <td>dif</td>
                        <td>#sol</td>
                        <td>cat</td>
                    </tr>
                    </tbody>
                </table>
            </main>
        </div>
    );
}

export default Problems;
