import React, {useState,useEffect,useContext} from 'react';
import Problem from '../../components/Problem/Problem';
import './Problems.css';
import AuthContext from '../../contexts/AuthContext';
// import ProblemReaderView from '../ProblemReaderView/ProblemReaderView';

async function saveProblem(credentials) {
    return fetch('http://localhost:8081/saveproblem', {
      method: 'PUT',
      headers: {
        'Content-Type': 'application/json'
      },
      body: JSON.stringify(credentials)
    })
      .then(data => data.json())
   }

function sortTableByQuestionCount() {
    let table = document.getElementById("problemsTable");
    let rows = Array.from(table.rows).slice(1); 

    rows.sort((a, b) => {
        let questiosA = parseInt(a.cells[4].innerText);
        let questionsB = parseInt(b.cells[4].innerText);
        return questionsB - questiosA; 
    });

    let tbody = table.tBodies[0];
    tbody.append(...rows);
}

const Problems = ({username, type}) => {
    const authContext = useContext(AuthContext);
    const [problems, setProblems] = useState([]);
    const [Title, setTitle] = useState();
    const [Content, setContent] = useState();
    const [Difficulty, setDifficulty] = useState();

    const handleSubmit = async e => {
        e.preventDefault();
        const token = await saveProblem({
            Title,
            Content,
            Difficulty,
            authContext
        });
        if (token.message)
            alert(token.message);
        else {
            alert(`Problem added`);
        };
    }

    useEffect(()=>{
        // console.log("Loading Data ...")

        sortTableByQuestionCount();
        if (type === 's')
            fetch(`http://localhost:8081/students/${username}/solved`)
                .then(response=>response.json())
                .then(data=>setProblems(data))
        else
            fetch(`http://localhost:8081/problems/${username}`)
                .then(response=>response.json())
                .then(data=>setProblems(data))


    },)

    // global
    if (!username)
        return (
            <div>
                <main>
                    <h1>All Problems</h1>
                    <table id="problemsTable">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Content</th>
                                <th>Difficulty</th>
                                <th>Category</th>
                                <th>Solved #</th>
                            </tr>
                        </thead>
                        <tbody>
                            {
                            problems.map((problem)=>{
                                return <Problem key={problem.id} title={problem.title} difficulty={problem.difficulty} solved={problem.solved} category={problem.category} content={problem.content} option_1={problem.option_1} option_2={problem.option_2} option_3={problem.option_3} option_4={problem.option_4}/>
                            })
                            }
                        </tbody>
                    </table>
                </main>
            </div>
    );

    // teacher profile
    if (type === 't')
        return (
            <div>
                <main>
                    <h2>Questions Designed</h2>
                    <table id="problemsTable">
                        <thead>
                            <tr>
                                <th>Title</th>
                                <th>Content</th>
                                <th>Difficulty</th>
                                <th>Solved #</th>
                                <th>Category</th>
                            </tr>
                        </thead>
                        <form onSubmit={handleSubmit}>
                        <tbody>
                            {
                            problems.map((problem)=>{
                                return <Problem key={problem.id} title={problem.title} difficulty={problem.difficulty} solved={problem.solved} category={problem.category} content={problem.content} option_1={problem.option_1} option_2={problem.option_2} option_3={problem.option_3} option_4={problem.option_4}/>
                            })
                            }
                        </tbody>
                        <thead>
                        <tr>
                            <td>
                                <label>
                                <p>Title</p>
                                <input type="text" onChange={e => setTitle(e.target.value)}/>
                                </label>
                            </td>
                            <td>
                                <label>
                                <p>Content</p>
                                <input type="text" onChange={e => setContent(e.target.value)}/>
                                </label>
                            </td>
                            <td>
                                <label>
                                <p>Difficulty</p>
                                <input type="text" placeholder='t for teacher, s for student' onChange={e => setDifficulty(e.target.value)}/>
                                </label>
                            </td>
                            <td>category</td>
                            <td>
                                <div>
                                <button type="submit">Submit</button>
                                </div>
                            </td>
                        </tr>
                        </thead>
                        </form>
                    </table>
                </main>
            </div>
        );

    // student profile
    return (
        <div>
            <main>
                <h2>Problems Solved</h2>
                <table id="problemsTable">
                    <thead>
                        <tr>
                            <th>Title</th>
                            <th>Content</th>
                            <th>Difficulty</th>
                            <th>Solved #</th>
                            <th>Category</th>
                        </tr>
                    </thead>
                    <tbody>
                        {
                        problems.map((problem)=>{
                            return <Problem key={problem.id} title={problem.title} difficulty={problem.difficulty} solved={problem.solved} category={problem.category} content={problem.content} option_1={problem.option_1} option_2={problem.option_2} option_3={problem.option_3} option_4={problem.option_4}/>
                        })
                        }
                    <tr>
                        <td>title</td>
                        <td><p>content</p></td>
                        <td>difficulty</td>
                        <td>category</td>
                        <td>add</td>
                    </tr>
                    </tbody>
                </table>
            </main>
        </div>
    );
}

export default Problems;
